package com.syntex.class8;

public class ClassTask3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// print numbers from 1 to 50 except those that are divisible by 3
		
	for (int k=1; k<=50; k++) {
		if (k%3==0) {
			continue;
		
	}
		System.out.print(k+"  ");
	}
	}
} 

	
